import React,{useState} from 'react'
import ReactDOM from 'react-dom'

const timeNow = new Date().toLocaleDateString()
const [state, setstate] = useState({
    timeNow
    })

const ref = ()=>{
    setstate({timeNow})
}

ReactDOM.render(
    <>
    <h1>Time : {timeNow} </h1>
    <button onClick={ref}></button>
    </>,

    document.getElementById('root')
)
