import React from 'react'

export default function State() {
    const [count, setcount] = useState(1)
    const IncNum = ()=>{
        setcount(count + 1)
        // console.log('clicked' , count++)
    }
    return (
        <div>
            <h1>{count}</h1>
            <button onClick={IncNum}>Click Me</button>
        </div>
    )
}
