import React from 'react'
import ReactDOM from 'react-dom'

const timeNow = new Date()
const hour = timeNow.getHours()
let greet = ''
let myStyle = ''
if(hour>=0 && hour <12){
     greet = 'morning'
     myStyle = {
         color : 'red'
     }
}
else if (hour>=12 && hour <6){
    greet = "afternoon"
    myStyle = {
        color : 'blue'
    }
}
else{
    greet = 'evening'
    myStyle = {
        color : 'green'
    }
}
ReactDOM.render(
    <>
    <h1>Good, <span style={myStyle}> {greet} </span></h1>
    </>,

    document.getElementById('root')
)
