import React, { useState } from 'react'
// import ToDolist from './ToDolists'
import '../sass/todolist/todolist.css'
import AddIcon from '@mui/icons-material/Add'
// import DeleteForeverIcon from '@mui/icons-material/DeleteForever';
// import EditIcon from '@mui/icons-material/Edit';

export default function Todolist() {
    const [init, setinit] = useState('')
    const [list, setlist] = useState([]);

    const addList = ()=>{
        setlist([init])
        setinit('')
    }

    return (
        <div className='todolist'>
            <div className="todo-center">
                <h1>To do List</h1>
                <input type="text" placeholder='Add Items...' onChange={(e)=>setinit(e.target.value)} value={init}/> 
                <button onClick={addList}><span><AddIcon/> </span></button>
                <ul>
                    {list.map((e,i)=>{
                        return(
                            <li style={{display : 'flex', justifyContent : 'space-around', border : '2px solid black', backgroundColor : 'whitesmoke', margin : '10px', borderRadius : '1rem', alignItems : 'center'}} ><EditIcon/><DeleteForeverIcon/></li>
                        )
                    })}

                </ul>
            </div>

        </div>
    )
}
