import React, { useState } from 'react'
import Notes from './Notes'
import '../sass/notes/note.css'

export default function Note() {
    const [Input, setInput] = useState({
        title : '',
        body : ''
    })

    const [Result, setResult] = useState([])


    const InputEvent= (event)=>{
        const {name,value} = event.target
        setInput((preValue)=>{
            return {
                ...preValue,
                [name] : value
            } 
        })
        console.log(Input)
    }

    const giveResult = (Input)=>{
        // alert(Input.title)
        setResult((prevValue)=>{
            return [...prevValue, Input]
        })
    }
    console.log(Result)
    return (
        <>
        <div className='note'>
             <h2><input type="text" placeholder='Add Title..' name="title" value={Input.title} onChange={InputEvent} /></h2>
            <p><textarea  id="" cols="" rows="" name="body" placeholder='Add NOte...' value={Input.body} onChange={InputEvent} ></textarea></p>
            <button onClick={giveResult}>Add</button>


        </div>
        <section className='note-section'>
           {Result.map((element,ind)=>{
               return <Notes
               key = {ind}
               id = {ind}
               title={element.title}
               body={element.body}
               />
           })}
        </section>
        </>
    )
}
