import React, { useState } from 'react'
// import ToDolist from './ToDolists'
import '../sass/todolist/todolist.css'
import AddIcon from '@mui/icons-material/Add'
import DeleteForeverIcon from '@mui/icons-material/DeleteForever';
import EditIcon from '@mui/icons-material/Edit';

export default function Todolist() {
    const [init, setinit] = useState('')
    const [list, setlist] = useState([]);

    const addList = ()=>{
        if(!init){

        }
        else{

            setlist([init,...list])
            setinit('')
        }
    }

    const deleteItem =(i)=>{
        const afterDelete = list.filter((ele,ind)=>{
            return i !== ind
        })
            setlist(afterDelete)
    }

    const editList =(id)=>{
        const afterEdit = list.filter((ele,ind)=>{
            return id === ind
        })
        init(afterEdit)
    }

    return (
        <div className='todolist'>
            <div className="todo-center">
                <h1>To do List</h1>
                <input type="text" placeholder='Add Items...' onChange={(e)=>setinit(e.target.value)} value={init}/> 
                <button onClick={addList}><span><AddIcon/> </span></button>
                <ul>
                    {list.map((e,i)=>{
                        return(
                            <li key={i} style={{display : 'flex', justifyContent : 'space-around', border : '2px solid black', backgroundColor : 'whitesmoke', margin : '10px', borderRadius : '1rem', alignItems : 'center'}} >
                                <EditIcon onClick={()=>editList(i)} />
                                <DeleteForeverIcon onClick={()=>deleteItem(i)} />
                            {e}
                            </li>
                        )
                    })}
              <DeleteForeverIcon />
                </ul>

            </div>

        </div>
    )
}
