import React from 'react'
import { useState } from 'react/cjs/react.development'
import '../sass/form/form.css'

export default function Form() {
    const [initText, finalText]= useState('')
    const [myName, fullName]=useState('')

    const [initTextTwo, finalTextTwo]= useState('')
    const [myNameTwo, fullNameTwo]=useState('')

    const inputText=(event)=>{
        finalText(event.target.value)
    }
    const onSubmits=(event)=>{
        event.preventDefault()
        fullName(initText)
        fullNameTwo(initTextTwo)
    }

    const inputTextTwo=(event)=>{
        finalTextTwo(event.target.value)
    }
    return (
        <div className='form'>
            <form onSubmit={onSubmits}>

            <h1>hello {myName} {myNameTwo}</h1>
            <input type="text"  onChange={inputText} value={initText}/>
            <input type="text"  onChange={inputTextTwo} value={initTextTwo}/>
            <button type='submit'>Click Me</button>
            </form>
        </div>
    )
}
