import React,{useState} from 'react'

export default function Notes() {
    const [data, setdata] = useState({
        title : '',
        content : ''
    })
    const [init, setinit] = useState([])
    const Inputdata = (event)=>{
        const {name,value} = event.target
        setdata((prevalue)=>{
            return {
                ...prevalue,
                [name] : value
            }
        })
        console.log(data)
    }
    const showOutput = (data)=>{
        setinit((nowValue)=>{
            return [...nowValue, data]
        })
    }
    return (
        <div>
            <h1><input type="text" name='title' value={data.value} onChange={Inputdata} /></h1>
            <p><textarea name="content" id="" cols="30" rows="10" value={data.content} onChange={Inputdata}></textarea></p>
            <button onClick={showOutput}>Show</button>
        </div>
    )
}
