import React,{useState} from 'react'
import '../sass/form/form.css'

export default function Form() {
    const [initText, setinitText] = useState( ()=>{
        return{
            id : '',
            pw : '' 
        }
    } )
    const result=(event)=>{
        event.preventDefault()
        const user = event.target.value
        const userName = event.target.name
        setinitText((preValue)=>{

                if(userName==='id'){
                    return{

                        id : user,
                        pw : preValue.pw
                    }
                } 
                else if (userName === 'pw'){
                    return{

                        id : preValue.user,
                        pw : user
                    }
                }

        })
    }
    return (
        <div className='form' onSubmit={result}>
            <form >
                <h1>Id : {initText.id}</h1>
                <h1>Pw : {initText.pw}</h1>
                <input type="text" name="id" onChange={initText} value = {initText.id}/>
                <input type="text" name="pw" onChange={initText} value={initText.pw}/>

                <button type='submit'>Submit</button>
            </form>
        </div>
    )
}
