import React from 'react'
import ReactDOM from 'react-dom'

let timeNow = {}
const morning =()=>{
    timeNow = new Date(2022, 1, 10, 1)
}
const afternoon =()=>{
    timeNow = new Date(2022, 1, 10, 15)
}
const evening =()=>{
    timeNow = new Date(2022, 1, 10, 19)
}
const hour = timeNow.getHours()
let greet = ''
let myStyle = ''
const center = {
    display : 'flex',
    justifyContent : 'center',
    alignItems : 'center'
}
if(hour>=0 && hour <12){
     greet = 'morning'
     myStyle = {
         color : 'red'
     }
}
else if (hour>=12 && hour <6){
    greet = "afternoon"
    myStyle = {
        color : 'blue'
    }
}
else{
    greet = 'evening'
    myStyle = {
        color : 'green'
    }
}
ReactDOM.render(
    <>
    <h1 style={center}>Good, <span style={myStyle}> {greet} </span></h1>
    <button onClick={morning}>Morning</button>
    <button onClick={afternoon}>AfterNoon</button>
    <button onClick={evening}>Evening</button>
    </>,

    document.getElementById('root')
)
